import { Button} from './components/Button';
import {Counter} from './components/Counter';
import './App.css';

/*
function App() {
  return (
    <div className="App">
      <Button onClick={() => console.log("clic")} label="Cliquez moi !"/>
      <Button onClick={() => console.log("button!")} label="clic"/>
    </div>
  );
}
