export class App extends React.Component {
  constructor(props) {
      super(props);
      this.state = {
          val : 1,
      }
  }

  render() {
      return (
          <div className="App">
              <Button label={this.state.val} onClick={() => {
                  this.setState(oldState => ({val: oldState.val + 1}), () => console.log(this.state.val));

              }}/>
          </div>
      );
  }
}


export default App;
